import React from 'react';
import { useRouter } from 'next/router';
import { NextPageContext } from 'next';
import axios from 'axios';
import Layout from '../components/MyLayout';

const Post = () => {
  const router = useRouter();

  console.log(router.co['/'].props.pageProps.posts);

  return (
    <Layout>
      <h2>
        post
        {router.query.id}
      </h2>
      <p>
        this is post #
        {router.query.title}
      </p>
    </Layout>
  );
};

Post.getInialProps = async (context: NextPageContext) => {
  const { id } = context.query;
  const post = await axios.get(`https://simple-blog-api.crew.red/posts/${id}`);

  return post;
};

export default Post;
